import React, { useState, useEffect } from 'react';

// ============================================================
// ROCK - Onchain Rock Paper Scissors
// A Farcaster Mini App for challenging friends to RPS duels
// ============================================================

const MOVES = {
  rock: { emoji: 'ü™®', beats: 'scissors', name: 'ROCK' },
  paper: { emoji: 'üìÑ', beats: 'rock', name: 'PAPER' },
  scissors: { emoji: '‚úÇÔ∏è', beats: 'paper', name: 'SCISSORS' }
};

const STAKE_OPTIONS = [1, 5, 10, 25];

// Mascot image embedded as base64
const MASCOT_URL = 'data:image/webp;base64,';

export default function RockApp() {
  const [screen, setScreen] = useState('splash');
  const [stake, setStake] = useState(5);
  const [playerMove, setPlayerMove] = useState(null);
  const [opponentMove, setOpponentMove] = useState(null);
  const [gameId, setGameId] = useState(null);
  const [isRevealing, setIsRevealing] = useState(false);
  const [result, setResult] = useState(null);
  const [winStreak, setWinStreak] = useState(0);

  // Splash screen auto-transition
  useEffect(() => {
    if (screen === 'splash') {
      const timer = setTimeout(() => {
        setScreen('home');
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [screen]);

  // Create game
  const createGame = () => {
    if (!playerMove) return;
    const id = Math.random().toString(36).substring(2, 8).toUpperCase();
    setGameId(id);
    setScreen('waiting');
  };

  // Accept challenge (simulated)
  const acceptChallenge = () => {
    if (!playerMove) return;
    const moves = ['rock', 'paper', 'scissors'];
    const oppMove = moves[Math.floor(Math.random() * 3)];
    setOpponentMove(oppMove);
    setScreen('reveal');
    startReveal(oppMove);
  };

  // Reveal animation
  const startReveal = (oppMove) => {
    setIsRevealing(true);
    setTimeout(() => {
      setIsRevealing(false);
      determineWinner(oppMove);
    }, 2500);
  };

  // Determine winner
  const determineWinner = (oppMove) => {
    if (playerMove === oppMove) {
      setResult('tie');
    } else if (MOVES[playerMove].beats === oppMove) {
      setResult('win');
      setWinStreak(prev => prev + 1);
    } else {
      setResult('lose');
      setWinStreak(0);
    }
    setScreen('result');
  };

  // Reset game
  const playAgain = () => {
    setPlayerMove(null);
    setOpponentMove(null);
    setGameId(null);
    setResult(null);
    setScreen('home');
  };

  return (
    <div className="app-container">
      <style>{globalStyles}</style>
      
      {screen === 'splash' && <SplashScreen />}
      
      {screen === 'home' && (
        <HomeScreen 
          winStreak={winStreak}
          onCreateGame={() => setScreen('create')}
          onJoinGame={() => setScreen('join')}
        />
      )}
      
      {screen === 'create' && (
        <CreateGameScreen
          stake={stake}
          setStake={setStake}
          playerMove={playerMove}
          setPlayerMove={setPlayerMove}
          onBack={() => { setPlayerMove(null); setScreen('home'); }}
          onCreate={createGame}
        />
      )}
      
      {screen === 'waiting' && (
        <WaitingScreen
          gameId={gameId}
          stake={stake}
          playerMove={playerMove}
          onCancel={playAgain}
        />
      )}
      
      {screen === 'join' && (
        <JoinGameScreen
          stake={stake}
          playerMove={playerMove}
          setPlayerMove={setPlayerMove}
          onBack={() => { setPlayerMove(null); setScreen('home'); }}
          onAccept={acceptChallenge}
        />
      )}
      
      {screen === 'reveal' && (
        <RevealScreen
          playerMove={playerMove}
          opponentMove={opponentMove}
          isRevealing={isRevealing}
        />
      )}
      
      {screen === 'result' && (
        <ResultScreen
          result={result}
          playerMove={playerMove}
          opponentMove={opponentMove}
          stake={stake}
          winStreak={winStreak}
          onPlayAgain={playAgain}
          onRematch={() => {
            setPlayerMove(null);
            setOpponentMove(null);
            setScreen('create');
          }}
        />
      )}
    </div>
  );
}

