// ============ RESULT SCREEN ============
function ResultScreen({ result, playerMove, opponentMove, stake, winStreak, onPlayAgain, onRematch }) {
  const config = {
    win: { title: 'YOU WIN!', subtitle: `+${(stake * 2 * 0.95).toFixed(2)} USDC`, className: 'result-win' },
    lose: { title: 'YOU LOSE', subtitle: `-${stake} USDC`, className: 'result-lose' },
    tie: { title: 'TIE GAME', subtitle: 'Stakes returned', className: 'result-tie' }
  }[result];

  return (
    <div className={`screen ${config.className}`}>
      <div className="screen-inner center">
        <div className="result-mascot">
          <img src={MASCOT_URL} alt="Rock mascot" className="mascot-result" />
        </div>

        <h1 className="result-title">{config.title}</h1>
        <p className="result-subtitle">{config.subtitle}</p>

        {result === 'win' && winStreak > 1 && (
          <div className="streak-badge-result">
            üî• {winStreak} Win Streak!
          </div>
        )}

        <div className="result-moves">
          <div className="result-move-box">
            <span className="result-move-emoji">{MOVES[playerMove].emoji}</span>
            <span className="result-move-label">You</span>
          </div>
          <span className="result-vs">vs</span>
          <div className="result-move-box">
            <span className="result-move-emoji">{MOVES[opponentMove].emoji}</span>
            <span className="result-move-label">Them</span>
          </div>
        </div>

        <div className="result-actions">
          <button className="rematch-btn" onClick={onRematch}>
            üîÑ Rematch
          </button>
          
          <button className="share-btn">
            üì£ Share Result
          </button>
          
          <button className="home-btn" onClick={onPlayAgain}>
            Back to Home
          </button>
        </div>
      </div>
    </div>
  );
}

